import React from 'react';
import './QuoteCards.css';

function DisplayQuote ({ pers }) {
  return (
  <div className="QuoteCard">
     <img
       src={pers.image} 
       alt={pers.character} 
      />
     <div>
      <blockquote>{pers.quote}</blockquote>
      <p>
        <cite>{pers.character}</cite>
      </p>
    </div>
  </div>
  );
}

export default DisplayQuote;
